import React, { useState, useEffect } from 'react';
import './index.sass'
import Register from '../Register'
import Header from '../Header'
import Login from '../Login'
import { Route, withRouter, Redirect } from 'react-router-dom'
import { authenticateUser, registerUser, retrieveUser, searchAads} from '../../logic'

export default withRouter(function ({ history }) {
    // const [name, setName] = useState()
    // const [tasks, setTasks] = useState([])

    useEffect(() => {
        const { token } = sessionStorage;

        // (async () => {
        //     if (token) {
        //         const { name } = await retrieveUser(token)

        //         setName(name)

        //         await retrieveTasks(token)
        //     }
        // })()
    }, [sessionStorage.token])


    async function handleRegister(name, surname, city, address, email, password) {
        try {
            await registerUser(name, surname, city, address, email, password)

            history.push('/login')
        } catch (error) {
            console.error(error)
        }
    }

    async function handleLogin(email, password) {
        try {
            const token = await authenticateUser(email, password)

            sessionStorage.token = token
            console.log(token)

            history.push('/search')
        } catch (error) {
            console.error(error)
        }
    }

    function handleGoBack() { history.push('/') }

    function handleLogout() {
        sessionStorage.clear()

        handleGoBack()
    }

    const { token } = sessionStorage

    return <>
        {/* <Route exact path="/" render={() => <Header onBack={handleGoBack}/>} /> */}
        <Route exact path="/" render={() => <><Search/></>}/> 
        <Route exact path="/register" render={() => <><Header onBack={handleGoBack}/>  <Register onRegister={handleRegister}/></>}/> 
        <Route exact path="/login" render={() => <><Header onBack={handleGoBack}/> <Login onLogin={handleLogin}/></>}/> 

    </>
})
